import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import stones from '../images/stones.jpg'
import video from '../images/video.mp4'

import { useHistory } from 'react-router-dom';
import {
  user,
  logout,
  loggedoutMessage,
  getLoginMessage,
} from '../reducers/user';
import { useDispatch, useSelector } from 'react-redux';
import { QnA } from './QnA'
import { LogIn } from './LogIn';

const Main = styled.main`
background-image: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.5)), url(${stones});
background-size: cover;
display: flex; 
justify-content: center;

`
const Section = styled.section`
width: 80%; 
border-radius:10px;
background: white; 
font-family: 'Barlow', sans-serif;
font-size: 1.2rem; 
padding: 22px; 
margin: 24px;
display: flex; 
flex-direction: column;
align-items: center; 
`

const Article = styled.article`
display: flex; 
align-items: center;
flex-direction: column;
wrap: row-wrap; 
`
const TextCard = styled.div`
padding: 0 30px;
display: flex;
flex-direction: column;
align-items: center;
margin-bottom: 24px;
@media(min-width: 668px) {
  padding: 0 30px; 
}
`
const Title = styled.h1`
font-family: 'Julius Sans One', sans-serif;
color: #303e27;
font-size: 2.4rem;
text-align: center; 
margin: 26px;
`
const Video = styled.video`
width: 80%;
`


const Subheading = styled.h3`
font-family: 'Julius Sans One', sans-serif;
color: #666d21;
margin-bottom: 16px;
@media(min-width: 668px) {
  padding: 0 30px; 
}`

const USERS_URL = 'http://localhost:9001/users';

export const MembersPage = () => {
  const dispatch = useDispatch();
  const accessToken = useSelector((store) => store.user.login.accessToken);
  const userName = useSelector((store) => store.user.login.userName);
  const userId = useSelector((store) => store.user.login.userId);
  const errorMessage = useSelector((store) => store.user.login.errorMessage);
  const loginMessage = useSelector((store) => store.user.login.loginMessage);

  // const handleSecretStuff = (event) => {
  //   event.preventDefault();
  //   dispatch(getLoginMessage(userId, accessToken));
  // };

  if (!accessToken) {
    return (
      <Main><Section>
        <Title>VÃ¤lkommen, {userName}!</Title>
        <Article>
          <TextCard>
            <Subheading>Veckans video</Subheading>
            <Video width="320" height="240" controls>
              <source src={video} type="video/mp4"></source>
  Your browser does not support the video tag.
</Video>
          </TextCard>
          <TextCard>
            <QnA />
          </TextCard>
        </Article>
      </Section>
      </Main>      // <div>
      //   {errorMessage && <h4>Error Message : {`${errorMessage}`}</h4>}
      //   {loginMessage && <h4>Secret Message : {`${loginMessage}`}</h4>}
      //   <p>You are logged in now, {userName}</p>
      //   <p>{loginMessage}</p>
      //   <button type='submit' onClick={(e) => dispatch(logout())}>
      //     LOGGING OUT
      //   </button>
      //   <button type='submit' onClick={(e) => dispatch(getLoginMessage())}>
      //     SEE SECRET
      //   </button>
      // </div>
    );
  } else {
    return <LogIn />
  }
};
