import React, { useState } from 'react';
import styled from 'styled-components';
import { Link, useHistory } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { user, login, getLoginMessage } from '../reducers/user';
import { SignUp } from './SignUp';
import mountains from '../images/mountains.jpg';


const Cards = styled.section`
  display: flex; 
  height: 100vh; 
  align-items: center; 
  flex-direction: column;
  color: #5e5e5e; 
  font-family: 'Barlow', sans-serif;
  background-image: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.5)), url(${mountains});
  background-size: cover;
  margin-bottom: 40px; 
`
const LogInCard = styled.form`
display: flex;
 align-items: center;
flex-direction: column; 
justify-content: space-between;

background-color: rgba(0,0,0,0.2);
width: 50%;
height: 40%;
padding: 40px; 
margin: 40px;

box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);

@media(min-width: 668px)and(max-width: 1024px){
  flex-direction: row; 
  width: 70%; 
  height: 20%; 
  padding: 20px; 
}
@media(min-width: 1024px){
  width: 50%;
  flex-direction: row; 
  height: 20%; 
}


`
const SignUpCard = styled.form`
display: flex;

border: 2px solid white; 
border-radius: 16px; 
background-color: white;
width: 50%; 
height: 20%;
box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);

`
const Label = styled.label`
display: flex; 
flex-direction: column; 
`
const Text = styled.p`
margin-bottom: 16px;
`
const Input = styled.input`
  padding: 0.5em;
  height: 3rem;
  font-size: 1rem; 
  color:  darkblue;
  background: #ebedf9;
  border: none;
  border-radius: 10px;


  @media(min-width: 668px) {
    font-size: 1.2rem;
  }

`;

const Button = styled.button`
  padding: 0.8em;
  height: 3rem;
  font-size: 1rem; 
  color:  grey; 
  background: #ebedf9;
  border: white; 
  border-radius: 10px;
  &:hover {
    background: #b4bb72;
    color: white; 

  }

  @media(min-width: 668px) {
    font-size: 1.2rem;
    margin-right: 20px; 

  }
`



// const URL = 'http://localhost:8080/users';
// const URL = 'https://project-authorize.herokuapp.com/users';

export const LogIn = () => {
  const dispatch = useDispatch();
  const accessToken = useSelector((store) => store.user.login.accessToken);
  const loggedoutMessage = useSelector(
    (store) => store.user.login.loggedoutMessage
  );
  const logInMessage = useSelector(
    (store) => store.user.login.loggedoutMessage
  );
  const errorMessage = useSelector(
    (store) => store.user.login.errorMessage
  );
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [showSummary, setShowSummary] = useState(false);

  const handleLogin = (event) => {
    event.preventDefault();
    dispatch(login(name, password));
  };

  if (!accessToken) {
    return (
      <section>
        {!showSummary && (
          <Cards>

            <LogInCard onSubmit={handleLogin}>

              <Label>

                <Input
                  placeholder="Logga in med ditt namn"
                  value={name}
                  onChange={(event) => setName(event.target.value)}
                  required
                />
              </Label>
              <Label>

                <Input
                  placeholder="LÃ¶senord"
                  type='password'
                  value={password}
                  required
                  onChange={(event) => setPassword(event.target.value)}
                />
              </Label>

              <Button type='submit'>Logga in</Button>
            </LogInCard>

            <SignUpCard> <p>
              Are you not a member yet? Please{' '}
              <SignUp />
            </p></SignUpCard>
          </Cards>
        )}



        {errorMessage && <h4>Error Message : {`${errorMessage}`}</h4>}
        {loggedoutMessage && <h4>you are now logged out!</h4>}
      </section>
    );
  } else {
    return (
      <div>
        <p>You have logged in!</p>;<p>{logInMessage}</p>
      </div>
    );
  }
};
